import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const formStyle = {
  width: "300px",
  margin: "auto",
  padding: "20px",
  borderRadius: "8px",
  marginTop: "50px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  background: "linear-gradient(to right, #ff4d4d, #ffcc00)",
};

const inputStyle = {
  width: "100%",
  padding: "10px",
  margin: "8px 0",
  display: "inline-block",
  border: "1px solid #ccc",
  boxSizing: "border-box",
  borderRadius: "4px",
};

const buttonStyle = {
  backgroundColor: "#4CAF50",
  color: "white",
  padding: "14px 20px",
  margin: "8px 0",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
  width: "100%",
};

function Signup() {
  const history = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  async function submit(e) {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:8000/signup", {
        email,
        password,
      });

      if (response.data === "exist") {
        alert("User already exists");
      } else if (response.data === "notexist") {
        history("/home", { state: { id: email } });
      }
    } catch (error) {
      alert("Error occurred during signup");
      console.error(error);
    }
  }

  return (
    <div style={formStyle} className="login">
      <h1 style={{ textAlign: "center", color: "white" }}>Signup</h1>
      <form action="POST">
        <input
          style={inputStyle}
          type="email"
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
        />
        <input
          style={inputStyle}
          type="password"
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
        />
        <button style={buttonStyle} type="submit" onClick={submit}>
          Signup
        </button>
      </form>
      <br />
      <p style={{ textAlign: "center", color: "white" }}>OR</p>
      <br />
      <Link style={{ textAlign: "center", display: "block", color: "white" }} to="/">
        Login Page
      </Link>
    </div>
  );
}

export default Signup;
