import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const Carousel = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        margin: 10,
        // Add padding to each slide
        prevArrow: <CustomPrevArrow />,
        nextArrow: <CustomNextArrow />,
    };

    // Custom arrow component for the previous arrow
    const CustomPrevArrow = (props) => {
        const { className, style, onClick } = props;
        return (
            <div
                className={className}
                style={{ ...style, left: '-30px', zIndex: 1 }} // Adjust the positioning as needed
                onClick={onClick}
            />
        );
    };

    // Custom arrow component for the next arrow
    const CustomNextArrow = (props) => {
        const { className, style, onClick } = props;
        return (
            <div
                className={className}
                style={{ ...style, right: '-30px', zIndex: 1 }} // Adjust the positioning as needed
                onClick={onClick}
            />
        );
    };

    return (
        <div className='slider-main'>
            <Slider {...settings}>
                <div>
                    <img
                        className="carousel-image"
                        src="https://cdn.grofers.com/cdn-cgi/image/f=auto,fit=scale-down,q=70,metadata=none,w=2700/layout-engine/2022-05/Group-33704.jpg"
                        alt="Banner 1"
                    />
                </div>
                <div>
                    <img
                        className="carousel-image"
                        src="https://cdn.grofers.com/cdn-cgi/image/f=auto,fit=scale-down,q=70,metadata=none,w=2700/layout-engine/2022-05/Group-33704.jpg"
                        alt="Banner 2"
                    />
                </div>
                {/* Add more images as needed */}
            </Slider>
        </div>
    );
};

export default Carousel;
