import React from "react";
import { Link } from "react-router-dom";

const NavBar = () => (
  <nav style={{ background: "#333", padding: "10px", color: "white" }}>
    <Link to="/">Home</Link>
    <Link to="/contact">Contact</Link>
    <div className="dropdown">
      <button className="dropbtn">Products</button>
      <div className="dropdown-content">
        <Link to="/products/electronics">Electronics</Link>
        <Link to="/products/clothing">Clothing</Link>
        {/* Add more product categories */}
      </div>
    </div>
  </nav>
);

const Carousel = () => (
  <div style={{ width: "80%", margin: "auto", marginTop: "20px" }}>
    {/* Your carousel component goes here */}
    <p>Placeholder for Carousel</p>
  </div>
);

const GroceryItemCard = ({ itemName, price, imageUrl }) => (
  <div style={{ width: "200px", margin: "10px", border: "1px solid #ddd", padding: "10px" }}>
    <img src={imageUrl} alt={itemName} style={{ width: "100%" }} />
    <h3>{itemName}</h3>
    <p>${price}</p>
    <button>Add to Cart</button>
  </div>
);

const GroceryItems = () => (
  <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
    <GroceryItemCard itemName="Item 1" price="10" imageUrl="item1.jpg" />
    <GroceryItemCard itemName="Item 2" price="15" imageUrl="item2.jpg" />
    {/* Add more GroceryItemCard components */}
  </div>
);

const Footer = () => (
  <footer style={{ background: "#333", padding: "10px", color: "white", marginTop: "20px" }}>
    <p>&copy; 2023 Your Company Name. All rights reserved.</p>
  </footer>
);

const Home = () => (
  <div className="homepage">
    <NavBar />
    <h1 style={{ textAlign: "center", marginTop: "20px" }}>Hello and welcome to the home</h1>
    <Carousel />
    <h2 style={{ textAlign: "center", marginTop: "20px" }}>Featured Grocery Items</h2>
    <GroceryItems />
    <Footer />
  </div>
);

export default Home;
