// Home.js

import React from "react";
import { Link } from "react-router-dom";
import "./Home.css"; // Import your CSS file

const NavBar = () => (
  <nav className="navbar">
    <Link to="/">Home</Link>
    <Link to="/contact">Contact</Link>
    <div className="dropdown">
      <button className="dropbtn">Products</button>
      <div className="dropdown-content">
        <Link to="/category1">Category 1</Link>
        <Link to="/category2">Category 2</Link>
        {/* Add more categories as needed */}
      </div>
    </div>
  </nav>
);

const Carousel = () => (
  <div className="carousel-container">
    {/* Add your carousel content */}
    <p>Carousel</p>
  </div>
);

const GroceryItemCard = ({ itemName, price, imageUrl }) => (
  <div className="grocery-item-card">
    <img src={imageUrl} alt={itemName} />
    <h3>{itemName}</h3>
    <p>${price}</p>
  </div>
);

const GroceryItems = () => (
  <div className="grocery-items-container">
    {/* Add your grocery items */}
    <GroceryItemCard
      itemName="Apple"
      price="2.99"
      imageUrl="https://cdn.grofers.com/cdn-cgi/image/f=auto,fit=scale-down,q=70,metadata=none,w=140/app/images/products/sliding_image/425788a.jpg?ts=1692688626" // Replace with the actual URL
    />
    <GroceryItemCard
      itemName="Banana"
      price="1.99"
      imageUrl="https://cdn.grofers.com/cdn-cgi/image/f=auto,fit=scale-down,q=70,metadata=none,w=540/app/images/products/sliding_image/355051a.jpg?ts=1687326264" // Replace with the actual URL
    />
    {/* Add more grocery items as needed */}
  </div>
);

const Footer = () => (
  <footer className="footer">
    {/* Add your footer content */}
    <p>Contact us at: contact@example.com</p>
  </footer>
);

const Home = () => (
  <div className="homepage">
    <NavBar />
    <h1 className="welcome-heading">Hello and welcome to the home</h1>
    <Carousel />
    <h2 className="featured-items-heading">Featured Grocery Items</h2>
    <GroceryItems />
    <Footer />
  </div>
);

export default Home;
